import QQMapWX from '@/libs/qqmap-wx-jssdk.min'
import config from '@/_start/min/config'

export default {
	methods: {
		getLocation () {
			let qqmapsdk = new QQMapWX({
		        key: config.mapKey
		    });
			return new Promise((resolve, reject) => {
				qqmapsdk.reverseGeocoder({
			        success: res => {
			        	resolve(res);
			        },
			        fail: res => {
			        	reject(res);
			        }
			    });
			});
	    }
	}
}