export default {
	data() {
	    return {
	      storeList: []
	    }
	},
	methods: {
		lazyLoad (selector) {
		    // 图片懒加载
		    const query = wx.createSelectorQuery();
		    query.selectAll(selector).boundingClientRect(ret => {
		      ret.forEach((item, index) => {
		        if (item.top <= this.screenH) {
		          this.$set(this.storeList[index], 'show', true);
		        }
		      });
		    }).exec();
	    }
	},
	mounted () {
	    wx.getSystemInfo({
	      success: res => {
	        this.screenH = res.screenHeight;
	      }
	    });
	},
}