export default {
	data() {
		return {
			isCode: true,	// 能否发送验证码
			isInit: true,	// 初始文字标志
			time: 60
		}
	},
	methods: {
		_reset () {
	    	this.isCode = false;
	        this.isInit = false;
	    },
		_timer (t) {
		  this.time = t;
		  this._reset();
	      let timer = setInterval(() => {
	      	this.time = t;
	        if (t > 1) {
	          t--;
	        } else {
	          // 时间到，可选重发
	          this.isCode = true;
	          this.isInit = true;
	          clearInterval(timer);
	        }
	      }, 1000);
	    }
	}
}